reg [7:0] data_mem_grain3[0:1];
reg [0:0] memadr_5;
always @(posedge sys_clk) begin
	if (data_port_we[3])
		data_mem_grain3[data_port_adr] <= data_port_dat_w[31:24];
	memadr_5 <= data_port_adr;
end

assign data_port_dat_r[31:24] = data_mem_grain3[memadr_5];

